import{_ as a,o as e,c as r,ai as i}from"./chunks/framework.C15FlLVL.js";const c=JSON.parse('{"title":"介绍","description":"","frontmatter":{},"headers":[],"relativePath":"guide/introduction.md","filePath":"guide/introduction.md"}'),o={name:"guide/introduction.md"};function n(l,t,d,h,s,u){return e(),r("div",null,[...t[0]||(t[0]=[i('<h1 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h1><h2 id="什么是-luna-ui" tabindex="-1">什么是 Luna UI？ <a class="header-anchor" href="#什么是-luna-ui" aria-label="Permalink to &quot;什么是 Luna UI？&quot;">​</a></h2><p>Luna UI 是一个基于 <strong>Vue 3</strong> + <strong>TypeScript</strong> 构建的现代化 UI 组件库，提供一套高质量、可定制的组件，帮助你快速搭建优美的 Web 应用。</p><h2 id="特性" tabindex="-1">特性 <a class="header-anchor" href="#特性" aria-label="Permalink to &quot;特性&quot;">​</a></h2><ul><li>🎨 <strong>主题定制</strong> — 基于 CSS Variables 的主题系统，支持亮色 / 暗色 / 跟随系统模式</li><li>🧩 <strong>Vue 3 + TypeScript</strong> — 完整的类型支持，基于 Composition API 开发</li><li>📦 <strong>按需引入</strong> — 支持 Tree-shaking，只打包你使用的组件</li><li>⚡ <strong>Vite 驱动</strong> — 基于 Vite 构建，开发体验极速</li><li>🎯 <strong>设计规范</strong> — 统一的设计语言，一致的交互体验</li></ul><h2 id="组件总览" tabindex="-1">组件总览 <a class="header-anchor" href="#组件总览" aria-label="Permalink to &quot;组件总览&quot;">​</a></h2><p>Luna UI 目前提供以下组件：</p><h3 id="通用" tabindex="-1">通用 <a class="header-anchor" href="#通用" aria-label="Permalink to &quot;通用&quot;">​</a></h3><table tabindex="0"><thead><tr><th>组件</th><th>说明</th></tr></thead><tbody><tr><td><a href="/HQ-UI/components/button.html">Button 按钮</a></td><td>6 种颜色 × 4 种变体 × 3 种尺寸，支持 loading、圆角、全宽等</td></tr></tbody></table><h3 id="数据录入" tabindex="-1">数据录入 <a class="header-anchor" href="#数据录入" aria-label="Permalink to &quot;数据录入&quot;">​</a></h3><table tabindex="0"><thead><tr><th>组件</th><th>说明</th></tr></thead><tbody><tr><td><a href="/HQ-UI/components/input.html">Input 输入框</a></td><td>支持 v-model、可清除、前后缀插槽、校验状态</td></tr><tr><td><a href="/HQ-UI/components/switch.html">Switch 开关</a></td><td>支持 v-model、标签、loading 状态</td></tr></tbody></table><h2 id="设计理念" tabindex="-1">设计理念 <a class="header-anchor" href="#设计理念" aria-label="Permalink to &quot;设计理念&quot;">​</a></h2><p>Luna UI 的设计遵循以下原则：</p><ol><li><strong>简洁优先</strong> — 组件 API 简洁直观，降低学习成本</li><li><strong>可定制</strong> — 通过 CSS Variables 轻松定制主题，适配不同品牌风格</li><li><strong>一致性</strong> — 统一的尺寸体系（sm / md / lg）、颜色体系和交互模式</li><li><strong>可访问性</strong> — 关注键盘操作和语义化标签</li></ol><h2 id="浏览器兼容性" tabindex="-1">浏览器兼容性 <a class="header-anchor" href="#浏览器兼容性" aria-label="Permalink to &quot;浏览器兼容性&quot;">​</a></h2><p>Luna UI 支持所有现代浏览器：</p><table tabindex="0"><thead><tr><th>浏览器</th><th>版本</th></tr></thead><tbody><tr><td>Chrome</td><td>最近 2 个版本</td></tr><tr><td>Firefox</td><td>最近 2 个版本</td></tr><tr><td>Safari</td><td>最近 2 个版本</td></tr><tr><td>Edge</td><td>最近 2 个版本</td></tr></tbody></table><h2 id="相关链接" tabindex="-1">相关链接 <a class="header-anchor" href="#相关链接" aria-label="Permalink to &quot;相关链接&quot;">​</a></h2><ul><li>📖 <a href="/HQ-UI/guide/getting-started.html">快速上手</a> — 安装与基本使用</li><li>🎨 <a href="/HQ-UI/guide/theming.html">主题定制</a> — 自定义主题颜色和样式</li><li>🎮 <a href="https://hongqi-lgs.github.io/HQ-UI/" target="_blank" rel="noreferrer">在线 Playground</a> — 在线体验组件效果</li><li>💻 <a href="https://github.com/hongqi-lgs/HQ-UI" target="_blank" rel="noreferrer">GitHub 仓库</a> — 源代码与 Issue</li></ul>',19)])])}const b=a(o,[["render",n]]);export{c as __pageData,b as default};
